import { Table, Thead, Tbody, Tr, Th, Td, Button, Text } from "@chakra-ui/react"

class DataPage {
  constructor(data, pageSize, tableContainerId) {
    this.data = data;
    this.pageSize = pageSize;
    this.currentPage = 1;
    this.sortBy = '';
    this.sortOrder = 'asc';
    this.filterBy = '';
    this.filteredData = [];
    this.tableContainer = document.getElementById(tableContainerId);
    this.table = document.createElement(Table);
    this.prevButton = document.createElement(Button);
    this.nextButton = document.createElement(Button);
    this.currentPageText = document.createElement(Text);

    // Parse CSV data using Papa Parse
    Papa.parse("data.csv", {
      header: true,
      download: true,
      complete: (results) => {
        // Create header row
        const headerRow = this.table.insertRow();
        for (let i = 0; i < results.meta.fields.length; i++) {
          const headerCell = headerRow.insertCell();
          headerCell.innerHTML = results.meta.fields[i];

          // Add sorting functionality to header cells
          headerCell.addEventListener('click', () => {
            if (this.sortBy === results.meta.fields[i]) {
              this.sortOrder = this.sortOrder === 'asc' ? 'desc' : 'asc';
            } else {
              this.sortBy = results.meta.fields[i];
              this.sortOrder = 'asc';
            }
            this.renderTable();
          });
        }

        // Convert data to array of objects
        this.filteredData = results.data.map(row => {
          return Object.assign({}, row);
        });

        this.renderTable();
      }
    });

    // Create pagination controls
    this.prevButton.children[0].innerText = 'Prev';
    this.prevButton.colorScheme = 'teal';
    this.prevButton.disabled = true;
    this.prevButton.onClick = () => {
      this.currentPage -= 1;
      this.renderTable();
    };
    this.nextButton.children[0].innerText = 'Next';
    this.nextButton.colorScheme = 'teal';
    this.nextButton.disabled = true;
    this.nextButton.onClick = () => {
      this.currentPage += 1;
      this.renderTable();
    };
    this.currentPageText.innerText = `Page ${this.currentPage}`;
    this.tableContainer.appendChild(this.prevButton);
    this.tableContainer.appendChild(this.currentPageText);
    this.tableContainer.appendChild(this.nextButton);
    this.tableContainer.appendChild(this.table);
  }

  filterData() {
    if (this.filterBy === '') {
      this.filteredData = this.filteredData.slice();
    } else {
      this.filteredData = this.filteredData.filter(row => {
        return Object.values(row).some(val => {
          return val.toLowerCase().includes(this.filterBy.toLowerCase());
        });
      });
    }
  }

  sortData() {
    if (this.sortBy === '') {
      return;
    }
    this.filteredData.sort((a, b) => {
      if (this.sortOrder === 'asc') {
        return a[this.sortBy].localeCompare(b[this.sortBy]);
      } else {
        return b[this.sortBy].localeCompare(a[this.sortBy]);
      }
    });
  }

  renderTable() {
    // Apply filter and sort
    this.filterData();
    this.sortData();

    // Calculate pagination variables
    const totalPages = Math.ceil(this.filteredData.length / this.pageSize);
    const startIndex = (this.currentPage - 1) * this.pageSize;
    const endIndex = Math.min(startIndex + this.pageSize, this.filteredData.length);

    // Update table contents
    this.table.innerHTML = '';
    const headerRow = this.table.insertRow();
    for (let i = 0; i < this.filteredData.length; i++) {
      if (i >= startIndex && i < endIndex) {
        const row = this.table.insertRow();
        for (const key in this.filteredData[i]) {
          const cell = row.insertCell();
          cell.innerHTML = this.filteredData[i][key];
        }
      }
    }

    // Update pagination controls
    if (this.currentPage === 1) {
      this.prevButton.disabled = true;
    } else {
      this.prevButton.disabled = false;
    }
    if (this.currentPage === totalPages) {
      this.nextButton.disabled = true;
    } else {
      this.nextButton.disabled = false;
    }
    this.currentPageText.innerText = `Page ${this.currentPage} of ${totalPages}`;
  }

  search(filter) {
    this.filterBy = filter;
    this.currentPage = 1;
    this.renderTable();
  }
}

export default DataPage;
