import { useState, useEffect } from 'react';
import { Container, Heading, Table, Tbody, Tr, Th, Td } from '@chakra-ui/react';
import Papa from 'papaparse';

function DataPage() {
  const [data, setData] = useState([]);

  useEffect(() => {
    async function fetchData() {
      const response = await fetch('/Users/meghnagopalan/Desktop/mgopal7.github.io-peopleweave/my-app/data.csv');
      const reader = response.body.getReader();
      const result = await reader.read(); // read the entire stream
      const decoder = new TextDecoder('utf-8');
      const csvData = decoder.decode(result.value); // decode the result into a string
      const parsedData = Papa.parse(csvData, { header: true }).data; // parse the CSV string into an array of objects
      setData(parsedData);
    }

    fetchData();
  }, []);

  return (
    <Container maxW="container.lg" pt={8}>
      <Heading as="h1" mb={4}>
        My Data
      </Heading>
      {data && data.length > 0 ? (
        <Table variant="simple">
          <Tbody>
            <Tr>
              {Object.keys(data[0]).map((header) => (
                <Th key={header}>{header}</Th>
              ))}
            </Tr>
            {data.map((row, rowIndex) => (
              <Tr key={rowIndex}>
                {Object.values(row).map((cell, cellIndex) => (
                  <Td key={cellIndex}>{cell}</Td>
                ))}
              </Tr>
            ))}
          </Tbody>
        </Table>
      ) : (
        <div>Loading...</div>
      )}
    </Container>
  );
}

export default DataPage;
